(window.webpackJsonp=window.webpackJsonp||[]).push([[54],{378:function(_,v,t){"use strict";t.r(v);var p=t(7),a=Object(p.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("如何评价「科技」对人的影响？")]),_._v(" "),v("p",[_._v("“科技是中立的，重要的是看人如何使用。”")]),_._v(" "),v("p",[_._v("看到这个回答，你也许已在默默点头，它听起来是那么“客观”和滴水不漏。不过，让我再问你一个问题："),v("strong",[_._v("「桥梁」和「水坝」作为人类创造的技术，它们之间有区别吗？")])]),_._v(" "),v("p",[_._v("你可能会说，它们都是为人所用的技术，都让人类的生活过得更好。")]),_._v(" "),v("p",[_._v("可对于海德格尔来说，它们之间却有着本质的区别——")]),_._v(" "),v("p",[_._v("人类在河上架一座桥，桥能够让河的两岸连到一起。桥下面的水还是自然地流动，我们在设计桥的时候桥上有很多孔洞，孔洞让水位即便上涨了，桥也可以自然流淌。海德格尔将这种过程称为「带出」。")]),_._v(" "),v("p",[_._v("水坝却完全不一样——水坝的目的恰恰是要把河阻断。河流不再是河流，河流成为了水压，水坝能够让水压 "),v("strong",[_._v("“现成存在，持续在场，随时可用”")]),_._v("，这是海德格尔用的三个现代技术描述的词汇。在这个过程中，某种自然被「促逼」为某种储存物。")]),_._v(" "),v("p",[v("strong",[_._v("桥梁为了本身的存在，尽量不要把河流阻断，而水坝恰恰就要把河流阻断——这是古代技术和现代技术两个很大的区别。")])]),_._v(" "),v("p",[_._v("当我们用水坝把河流隔绝的时候，河流已经不再是河流了：我们发明了一个新的莱茵河，一个新的具有水压差的莱茵河，我们将它建立在现代工业体系之上。")]),_._v(" "),v("p",[_._v("我们不再能说“水电站建在莱茵河上”，而是“新的莱茵河建在现代水电站和现代工业体系之上”。")]),_._v(" "),v("p",[_._v("我新办了一个健身房的会籍，他们的进门方式是一台闸机，配发一个带有芯片的手环，在你用手环刷闸机的时候，闸机上的一个屏幕提示着它在识别你的脸，随后你的图像和系统里储存的照片match，你被获准进入。")]),_._v(" "),v("p",[_._v("这不是一间多么新潮或高档的健身房，他们当然有正当的理由这么做，如果我办了一个会籍，并把我的手环借给、租给陌生人使用，这当然是侵占他们的利益，如果要锁定一人一卡，人脸识别是防止健身房权益被人侵占的合理技术。")]),_._v(" "),v("p",[_._v("识别人脸的场景并不新鲜，海关、机场、支付宝都有刷脸支付。当然也有别的新闻，深圳市防止人们闯红灯过马路的识别，杭州小学中试行的课堂纪律识别。")]),_._v(" "),v("h2",{attrs:{id:"_01-技术的浪漫主义-「人」消失了"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_01-技术的浪漫主义-「人」消失了"}},[_._v("#")]),_._v(" 01 技术的浪漫主义：「人」消失了")]),_._v(" "),v("p",[_._v("技术到底在多大程度上影响了我们的生活？")]),_._v(" "),v("p",[_._v("直觉的答案当然是很大，不管从正面的角度来说，像我们津津乐道的无现金社会；还是当《黑镜》播放时，朋友圈中突然出现的忧心忡忡的文章，企业营销也乐此不疲的大谈“黑科技”，似乎其真是不可质疑的“第一生产力”。")]),_._v(" "),v("p",[_._v("但从大陆到香港，从这个无现金的天堂到一个还需要现金和实体RFID卡(Radio Frequency Identification，含有频率感应的芯片卡)支付的社会，其实并没有多大的不便。各个互联网企业都在频繁发力的语音助手功能，尤其是小米的产业链，只要使用“小爱同学”，你就可以开灯，将你的饮水机度数上调，或者看到现在敲你家门的人是谁，不知为何，我总觉得这些像是给失能人士使用的功能。我从未从这样的助手上得到真正的便利。")]),_._v(" "),v("p",[_._v("技术崇拜拥有一种神经质式的夸张，对它的鼓吹和对它的恐惧是同样的。")]),_._v(" "),v("p",[_._v("其中都包含着对于「人」本身的极大否定，在技术的描述中，我们是一个如此厌恶自己开灯的人，我们是一个会被揣在兜里的纸币弄糊涂的人，当然有时候技术也雄辩地证明，你们围棋下得一团糟，你们Dota打得同样不好。")]),_._v(" "),v("p",[_._v("这是技术的浪漫主义中的隐秘逻辑，油画技术的易保存性让我们记住了那么多的伟大画家，人类历史上可能最伟大的技术之一——弓箭，让我们知道了李广花荣，也塑造《指环王》中的精灵王子。但是重机枪手呢？我们可有什么知名的重机枪手么？")]),_._v(" "),v("p",[_._v("曾经，人是“工匠”，通过不同技术，世界上各行各业的工匠们得以浮现。而如今，在技术的浪潮下，「人」变得消失匿迹。")]),_._v(" "),v("p",[_._v("我们都听过一个说法，“技术是人的延伸”。像小提琴是小提琴手的延伸，刀剑是武士的延伸，一把锄头是农民手的延伸，鞋是我们脚的延伸。但今天的技术更不像是从我们身体往外的延伸，相反，用媒介理论家麦克卢汉（Marshall McLuhan）的话说，更像是一种“截肢”——自动机械臂是对工人的截肢，修音软件是对歌手喉咙的切割，摄像探头是对眼睛的夺取。")]),_._v(" "),v("p",[_._v("这里面的区分非常明显，前者我们还需要使用着我们的手和脚，而后者，是对我们眼耳鼻舌的根本否定。")]),_._v(" "),v("p",[_._v("在这里面，我们会看到一个明显的断裂。")]),_._v(" "),v("h2",{attrs:{id:"_02-大恐惧-我们欢迎转基因-憎恨基因编辑"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_02-大恐惧-我们欢迎转基因-憎恨基因编辑"}},[_._v("#")]),_._v(" 02 大恐惧：我们欢迎转基因，憎恨基因编辑")]),_._v(" "),v("p",[_._v("技术的浪漫主义的背后有一种情绪，这可能与一战战场上突然感受到重机枪嘶吼呼叫的士兵情绪类似，一种不可遏制的恐惧。")]),_._v(" "),v("p",[_._v("如果说我们恐惧微信支付和支付宝支付，这当然会让人觉得言过其实，不过且听我多说几句——")]),_._v(" "),v("p",[_._v("1968 年 11 月 6 日，曾负责运送 “小男孩” 部件的 “印第安纳波利斯” 号的舰长麦克维伊在获悉当年自己运输的竟是葬送 10 余万生灵的原子弹后，愧疚不已，饮弹自尽。我们当然也觉得原子弹可怕，不过由于其过于遥远，实在与我们毫不相干，如果你的周围也没有核电站，即使说“害怕”也是轻飘飘的。")]),_._v(" "),v("p",[_._v("但如果你在香港经营着一间找换实体人民币的钱庄呢？随时进行即时汇率兑换的微信支付或支付宝支付会不会让你害怕呢？到这里，也许就不那么言过其实了吧。")]),_._v(" "),v("p",[_._v("关于对技术的恐惧，在这里，我要说的不是“技术性失业”，不是技术会带来对某些社会分工的替代，因而我们会因为“失业”而害怕和恐惧。")]),_._v(" "),v("p",[_._v("我想说的是一种本能得多的东西。像是“基因编辑胚胎”带来的恐惧，不必进入科学原理的探讨和论证，我们将对“转基因蔬菜”的恐惧视作一种愚昧，但对“基因编辑人类胚胎”的恐惧却被视作正常，这实在是一种错乱的症状。恰恰反映出我们对技术的复杂情绪。")]),_._v(" "),v("p",[_._v("他们改造了蔬菜，好吧，看起来还是有好处的，而且他们列举出了一大堆的科学依据。但他们改造了人类！天啊。")]),_._v(" "),v("p",[_._v("有人好好想过这个中差距吗？这便是我说的那个明显的「断裂」，从“转基因蔬菜”到“基因编辑胚胎”的断裂，且不必说，基因编辑还是一个远比转基因保守的技术。让土豆中的直淀粉链不被激活，仅仅激活支淀粉链，这是对土豆的“延伸”还是“截肢”，我们确实毫不在意。但一个基因编辑的婴儿，不管他背后给出了多么合理的动机，抛除这个案例中的一切经济的，与信息隐瞒的瑕疵，也难掩我们的恐惧。")]),_._v(" "),v("p",[_._v("改造人类为何如此难以接受？这就是我说的那种“本能得多”的恐惧情绪，这与运送原子弹的恐惧，与一个找换现金的钱庄对电子支付的恐惧，拥有相同的结构。这代表一种根本性的，从底层逻辑上对自然中的人，这是个多么令人陌生的词汇，“自然中的人”，这说的就是我们全部的深刻否定。后启示录电影中总是刻画那些已然“非自然的人”，像是《疯狂麦克斯》中的War Boy，那也是我们恐惧的来源。")]),_._v(" "),v("p",[_._v("有人会觉得这是人的懦弱，难以拥抱新的改变，那我要说这是人的珍贵之处，他们珍视“自然”。这里的自然当然那不是指那种“保护自然环境”语境下的“物质自然”，而是在说“你这样做事情就不自然了”的语境下的那种，令人是其所是的“浑然天成”。")]),_._v(" "),v("p",[_._v("所以我想不论你找出多少理由，证明“基因编辑”人类实际上是无害的，安全的，我想都无法抹除这个本能的恐惧。")]),_._v(" "),v("h2",{attrs:{id:"_03-「显眼」与「隐藏」-真正可怕的是什么"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_03-「显眼」与「隐藏」-真正可怕的是什么"}},[_._v("#")]),_._v(" 03 「显眼」与「隐藏」：真正可怕的是什么？")]),_._v(" "),v("p",[_._v("基因编辑出新新人类，和我们应该毫无区别，他们轻易的融入在我们之中，但这丝毫没有减轻我们的恐惧，甚至还加剧了这样的恐惧。")]),_._v(" "),v("p",[_._v("为何我要提出基因编辑人类可以隐藏于我们之中的话题？让我们想另一个例子，想想乔布斯最初在舞台上津津乐道的iPhone交互，引得台下的阵阵惊呼，就像那个右滑解锁，如果你使用iPhone，现在你还可以轻易的看到那条“滑动解锁”的提示，但这句话你早已经熟视无睹，近乎透明了。")]),_._v(" "),v("p",[_._v("很多技术都是如此，像一架钢琴之于钢琴演奏家，在他安心演奏的时候，钢琴隐而不显，有的只有音乐。作为“人的延伸”的技术，不论我们多么强烈的让其显眼起来，最终他们都会「隐而不显」。像我现在正打字的这块机械键盘，键盘的销售商当然会从各个角度让这块键盘变得极其显眼夺目，但现在这一刻，有的只有文字和思绪，这块键盘丝毫不在我的注意之中，这恰恰是一块好键盘需要去实现的。")]),_._v(" "),v("p",[_._v("但这位隐于人群中的基因编辑人，这些藏在远东白桦林下发射井中的核弹，这些无处不在的街角的摄像头，其本意就是隐藏。但却处处牵动着我们紧张的神经，仅仅从这样的一个微妙的「明显」中，我们就能够感觉到上面所言的那种“明显的断裂”是什么意思了。一方面是那些使用之前耀眼夺目，使用之中立即隐而不显的技术，一方面是那些悄悄的布置和安装，也不想引起你的任何注意，但就是让你坐立不安的技术。")]),_._v(" "),v("p",[_._v("这样的一种“牵扯”，构成了一切技术浪漫主义的内核，不管是鼓吹的，还是忧心忡忡的，翻翻你漫长的微信聊天记录，打开抖音，以最快的速度无限的往下划上几分钟；想想你的那个快递小盒子，在到达你手上之前，曾经置身于一个空间，那是一个山一样巨大的仓库，那里面有1000万个这样的箱子，这不可怕吗？")]),_._v(" "),v("p",[_._v("这些被「隐藏」起来的东西，不管是某个AI的算法，健身房的人脸识别，遥远的巨大仓库，在你的抖音的Feed流下安安静静排队等待被播放的难以计数的视频，正是这些「本意隐藏而如此明显」的东西，构成了我们的恐惧。")]),_._v(" "),v("h2",{attrs:{id:"_04-后xx时代-的结束"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_04-后xx时代-的结束"}},[_._v("#")]),_._v(" 04 “后XX时代”的结束")]),_._v(" "),v("p",[_._v("我们曾经在一个模棱两可的时代里面，也许现在也还在。在那个时代里面，我们对技术有着复杂的情绪，原子弹确实可怕，核电站的事故亦是，钉钉自然也是烦人的，微信把人们的关系搞得有点奇怪，但总体上还是方便的。医学科技提高了平均寿命，袁隆平和他的技术据说养活了我们，我们也为朋友圈上不知所云的科技英雄脑热。")]),_._v(" "),v("p",[_._v("我们管这个叫“后XX时代”，有很多不同的命名方法，专门描述这种暧昧而模棱两可的态度。在这个时代里面，每个人都说着：“科技是中立的，重要的是看人如何使用。”多么正确而滴水不漏的说法，我想现在还会有不少人抱持这样的信念。")]),_._v(" "),v("p",[_._v("不过我想这个时代结束了，虽然最终水落石出的答案不是“科技真好”，甚至也不是“科技真坏”，我们将最先感受到的是「科技真显眼啊」，随后是那种本能的恐惧。我想大多数人知道，波音787max的事故起因是一个为了让飞机更安全的空速管自动技术，如果你进一步去了解一架现代客机上到底有多少这样的自动化技术，且还在加速的布置这样的自动化技术，你还敢坐飞机吗？")]),_._v(" "),v("p",[_._v("这是一个寓言，一个有点早的寓言，当然了，如果不早，怎么能够叫做寓言呢？")]),_._v(" "),v("h2",{attrs:{id:"结语-那个健身房的故事"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#结语-那个健身房的故事"}},[_._v("#")]),_._v(" 结语： 那个健身房的故事")]),_._v(" "),v("p",[_._v("又有一天我去那个健身房，闸机出了故障，它就那样坏掉了。但我仍然正常地进出那个健身房，甚至连手环也不用刷了，因为那个地方常去的顾客实在就是那么一百多位，健身房的工作人员不需要记性惊人，其实就早已认出了有会籍的人，并在进门时热情地打招呼。")]),_._v(" "),v("p",[_._v("那个闸机坏到现在，也还没有修，我想他们也认为并无必要了吧。")])])}),[],!1,null,null,null);v.default=a.exports}}]);