(window.webpackJsonp=window.webpackJsonp||[]).push([[62],{385:function(_,v,p){"use strict";p.r(v);var t=p(7),a=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("blockquote",[v("p",[_._v('"\n今天人们确实是一边以人类从未有过的密度娱乐着，一边深深地感受着娱乐后的空虚和无聊。')]),_._v(" "),v("p",[_._v("娱乐的生活是扼死好奇心的，是“改变”的敌人，是一间舒适的“空调房”，那里面不光提供快感，也提供自我陶醉。")]),_._v(" "),v("p",[_._v('但娱乐确实并不让人真正满足，它是一种永远吃不饱的假粮食，是安慰剂。\n"')])]),_._v(" "),v("p",[_._v("上一篇关于《寄生虫》的文章（点击蓝字即可阅读），我提出一个观点：“2011年《一次别离》后没有好电影”，有读者难以认同，也因此希望我给出我的一份影单推荐。")]),_._v(" "),v("p",[_._v("但我觉得仅仅提供一个影单实在意义不大，现在各种电影推荐铺天盖地，比起这些电影是什么，“这些电影为何好”可能更值得一说。")]),_._v(" "),v("p",[_._v("另外，“为何好”当然与“电影”在我们生活中的角色高度相关，以及我是如何看待“电影”这种媒介形式。")]),_._v(" "),v("p",[_._v("我的核心观点是：珍惜电影，因为电影是少有的能让我们“改变”的契机，而“改变”在今天实在是太难太难。")]),_._v(" "),v("p",[_._v("看到这里，我想一个很直觉的疑惑就是：你说的是什么“改变”？为什么我们要“改变”？")]),_._v(" "),v("h2",{attrs:{id:"_1-电影热时代的-冷眼"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_1-电影热时代的-冷眼"}},[_._v("#")]),_._v(" 1. 电影热时代的“冷眼”")]),_._v(" "),v("p",[_._v("不需要列举票房或银幕数据，电影无疑已经成为最主流的娱乐方式，在当今城市人的生活中，电影实在扮演重要角色。但这个“角色”是什么，是一个值得好好思索和玩味的东西。")]),_._v(" "),v("p",[_._v("有一次我在星巴克不小心听到旁边一对男女聊天，他们大概是相亲见面，正在交换彼此的生活，当然不可避免聊到电影（我想这也是电影扮演重要角色的一个旁证）。")]),_._v(" "),v("p",[_._v("他们很快就电影的问题达成了共识：都不喜欢文戏，不喜欢文艺电影。当时正是《地球最后的夜晚》上映之时，他们也都各自看过了这部电影，也是一致地大呼上当。他们还谈到对漫威电影的喜爱，还谈到在B站（哔哩哔哩）以高倍速观看电影的经历。")]),_._v(" "),v("p",[_._v("你可能认为我又要来进行一番现代性娱乐批判，那实在没有必要，也陈词滥调了。批判人们“缺乏耐心”或“过度娱乐”的文章很多，在绝大多数情况下，人们点赞并转发这些文章，在人前聊着电影大师，经典老片，回家还是以高倍速在B站刷娱乐电影。")]),_._v(" "),v("p",[_._v("所以今天完全不是要写上一篇占据“鄙视链”高位的电影文章，然后我们大家在这里击掌相庆，过过瘾。我要说的比这更复杂一些。")]),_._v(" "),v("p",[_._v("在星巴克听到的这个例子很直白，在今天的主流语境下，也是一种“消极文化”的叙述方式。但换个叙述方式，立即就是个更好接受的版本，就是上一篇《寄生虫》文章最高赞的评论，评论的内容是：")]),_._v(" "),v("p",[_._v("“作者（注：指的我）应该不是电影行业从业者，商业电影编剧有固定模式，这个片导演自己说了，是个类型片，也就是商业片，本身没指望拿奖，背后资本一来二去拿奖不能怪电影本身，类型电影不该引来这么大偏见。”")]),_._v(" "),v("p",[_._v("言下之意，我上一篇对《寄生虫》的批评过分了，这片的定位就是“类型片”，为何要以非类型片的方式来要求？这就如同说，我用日料的标准来评价川菜，嫌川菜口味太重，是有失偏颇的。")]),_._v(" "),v("h2",{attrs:{id:"_2-类型片-电影业曾经难以启齿的-配角"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_2-类型片-电影业曾经难以启齿的-配角"}},[_._v("#")]),_._v(" 2. 类型片—— 电影业曾经难以启齿的“配角”")]),_._v(" "),v("p",[_._v("那就从类型片开始聊聊电影这个话题。")]),_._v(" "),v("p",[_._v("这是我们今天看到的绝大多数电影的特征，是司空见惯的。很多在今日司空见惯的东西，远没有它看上去那么平常合理。")]),_._v(" "),v("p",[_._v("类型片当然是好莱坞产物，其起源早自1930年代，那正是大萧条的前夜，很快第二次世界大战将因为大萧条而打响。")]),_._v(" "),v("p",[_._v("我们也明白，在萧条来临的前夜，人们总是最强烈地感受到强盛和欣欣向荣。")]),_._v(" "),v("p",[_._v("彼时好莱坞一片繁荣，竞争激烈。精明的发行方想出一个在今天依然有效的促销策略——“买一赠一”，即Double Feature发行制度：一张电影票，可以看两部电影，第二部短小一些，预算较低，但是依然精彩。")]),_._v(" "),v("p",[_._v("这就是所谓的B级片，“B”的命名来源于电影记录磁盘的A面B面，当时附在正片B面的短小刺激、用于搭售的电影，是类型片的起源。")]),_._v(" "),v("p",[_._v("这些电影预算少，时长短，但必须娱乐性十足，不然也无法完成搭售价值。请注意，“高效地提供具有娱乐价值的内容”，几乎必然地意味着“套路化”，早期的电影套路（类型）由此产生，例如西部片、惊悚科幻、恐怖片，这就是最早的类型片。")]),_._v(" "),v("p",[_._v("在好莱坞的体系内，与B-MOVIE类型片相对的，是Arthouse Film。这里“art”没有“艺术”的意谓，实际上在西文传统里“art”更多是指“技艺”，像人文教育之“人文”就是“liberal arts”，在这个词汇里面与艺术完全无关。")]),_._v(" "),v("p",[_._v("Arthouse Film就是指那种技术精湛，具有开创性的电影，可想在1930年时代，还未有成熟的电影工业，凡是大手笔投资的电影，都具有极强的开创性。")]),_._v(" "),v("p",[_._v("例如迪卡普里奥主演的《飞行家》，所描绘的就是美国航空大亨霍华德·休斯，他在1930年极尽奢侈的电影《地狱天使》就是一部极具开创性的电影，可以说是今天“战争”类型片的开创者。")]),_._v(" "),v("p",[_._v("所以对于电影，大概可以由一个简单的二分法作为我们进一步探究的结构。")]),_._v(" "),v("p",[_._v("电影确实可以分作“类型片”和“非类型片”，在娱乐性电影受众最多的地方，当然需要批量性地生产“类型片”。我们熟悉的例子是印度宝莱坞，更熟悉的例子是前两年风靡的“网大（网络大电影）”，各视频网站的网大都是十足的类型片，在其中甚至可以单独列出“西游魔幻”这一细分类型。")]),_._v(" "),v("p",[_._v("类型片就是这么一种低风险，观看门槛较低的，适合于商业推广的电影类型。")]),_._v(" "),v("h2",{attrs:{id:"_3-挺直腰杆的类型片-消遣与严肃成了-平行-的趣味"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_3-挺直腰杆的类型片-消遣与严肃成了-平行-的趣味"}},[_._v("#")]),_._v(" 3. 挺直腰杆的类型片—— 消遣与严肃成了“平行”的趣味")]),_._v(" "),v("p",[_._v("类型片本是电影序列中的“低级产物”，我们听过不少B级片出生的导演，摸爬滚打也要进入Arthouse的序列。之前观看类型片，也是难登大雅之堂的，这是人们不值一提的消遣和爱好。（我知道现在有很多B级片爱好者，看到这里肯定已经要暴跳如雷的为B级片正名了，你们先等我再多说几句。）")]),_._v(" "),v("p",[_._v("但今天，“类型片”俨然已经是可以为一部电影“正名”的概念。")]),_._v(" "),v("p",[_._v("电影再不是“具有开创性的电影”到“搭售的娱乐电影”这样从高到低的序列，在今天，“提供娱乐价值的类型片”和“提供所谓思想价值的非类型片”不过是两种平行的类别。")]),_._v(" "),v("p",[_._v("这是消遣与严肃平等的时代，消遣和严肃仅仅是两种平行的趣味，或是一个人不同时期的不同需求。")]),_._v(" "),v("p",[_._v("还是用饮食的比喻，就像是一个人这段时间好吃咸，下一段时间好吃甜，甜咸并无高低之分。")]),_._v(" "),v("p",[_._v("在这里，我上一篇文章是何种“偏见”就清楚了，这就是一种“非类型片更好，非娱乐性电影比娱乐电影更好”的偏见，类似于一种认为“日料一定比川菜好”的偏见。")]),_._v(" "),v("p",[_._v("可以看出，从一种高低序列，到平行序列，其原因不是因为80年过去，“娱乐”发生了什么变化，获得了什么了不得的价值。")]),_._v(" "),v("p",[_._v("当然是因为“非娱乐”在过去有巨大的价值，到今天，这个价值我们已经感受不到了。")]),_._v(" "),v("h2",{attrs:{id:"_4-严肃-vs-娱乐-潜移默化-的作用也无法为严肃遮羞"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_4-严肃-vs-娱乐-潜移默化-的作用也无法为严肃遮羞"}},[_._v("#")]),_._v(" 4. 严肃 VS 娱乐 “潜移默化”的作用也无法为严肃遮羞")]),_._v(" "),v("p",[_._v("严肃的东西有什么价值？在今天有一种流俗的观点，认为严肃之物，或是艺术，对人有一种潜移默化的作用，艺术的功能是一种熏陶和感染。")]),_._v(" "),v("p",[_._v("可是在我看来，这是“艺术无用论”最后的遮羞布，实在编不出严肃之物与艺术的作用，就辩称虽然你看不到摸不着，但其实在悄悄地发挥着最大的作用。")]),_._v(" "),v("p",[_._v("人们有没有接受这套说辞呢？我想大家已经用脚投票，彻底地证明了。在艺术从业者还羞羞答答地主张这种隐秘的、感应式的作用时，人们早已抛弃了这套玄学。")]),_._v(" "),v("p",[_._v("现代生活充满了可以立即起效之物，娱乐电影可以让人当下感受乐趣，游戏、美食亦是，而且它们越来越快，越来越密集，密集到抖音这样的程度。")]),_._v(" "),v("p",[_._v("也别觉得“娱乐”一定是嘻嘻哈哈，“娱乐”也可以让你哭哭啼啼，很多极尽煽情能事的灾难、爱情类型片，在里面声光电全开，让人哭出来，同样是一种娱乐。")]),_._v(" "),v("p",[_._v("也别觉得“娱乐”一定是笑或哭，很多极尽“符号搬弄”能事的犯罪、剧情类型片，在里面起承转合，让人看完大呼“烧脑”，感觉自己智商似乎比旁人高出几分，同样是一种娱乐。")]),_._v(" "),v("p",[_._v("也别觉得“娱乐”一定是一种完全自利的情绪，很多看上去揭露社会问题的电影，在里面悲伤的疾呼，让人看完大呼“天道不公”，感觉自己社会责任心雄雄，同样是一种娱乐。")]),_._v(" "),v("p",[_._v("这些都不需要“潜移默化”，都是直来直去的情感，不管是哭是笑，是聪明还是一种自感伟大的陶醉，娱乐可以给人这么多东西。哪还轮得着“潜移默化”来发挥作用。")]),_._v(" "),v("h2",{attrs:{id:"_5-哪里还有-新东西"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_5-哪里还有-新东西"}},[_._v("#")]),_._v(" 5. 哪里还有“新东西”？")]),_._v(" "),v("p",[_._v("你可能开始觉得我胡搅蛮缠了，为什么把“烧脑的”，“感动的”，甚至“责任心”的，都叫做“娱乐”？")]),_._v(" "),v("p",[_._v("我想更进一步，提出一个有点难接受，但非常典型的例子了。《我不是药神》为什么是一部彻头彻尾的娱乐片，和《寄生虫》一样。")]),_._v(" "),v("p",[_._v("人人都知道病痛的可怕，生命的可贵，在严格的法与情感的人命之间，主角给病人带来“生”的可能，这是一种“善”，和这种“善”相比，走私的“恶”显得并不重要。")]),_._v(" "),v("p",[_._v("这是看完电影后，人们会得到的新观念，还是看电影前，人们就早已经知道的东西？")]),_._v(" "),v("p",[_._v("电影仅仅是将这个东西以更激烈的冲突和更煽情的情节予以呈现。《寄生虫》也是一样，富人看不起穷人，穷人也非善类，有道德瑕疵，这是人们看电影得出的新感受，还是看电影前，人们早就明白的？")]),_._v(" "),v("p",[_._v("这就是为什么我说这类电影都是“娱乐”的原因，《前任》中的“爱情真苦”和《我不是药神》中的“病人真可怜”，“小人物真伟大”才是平行的东西，这是人们在生活中最表浅的“担忧”和“自我感动”，电影将其呈现，让人在影院高潮连连，像是得了人生的大感悟。")]),_._v(" "),v("p",[_._v("然后呢？有什么新东西吗？请问《我不是药神》中有任何令人困惑的情节吗？哪一处剧情让人难以理解？")]),_._v(" "),v("p",[_._v("如果所有这一切都是早已经被我们理解透了的“社会现实”，里面自然不会有新东西，没有新东西，当然我们也很难从中有任何改变。")]),_._v(" "),v("p",[_._v("我想请诸位回想，你可曾看过什么让你有些疑惑的电影，不是那种故弄玄虚的cult片。如果看过杨德昌电影《一一》的人，应该明白我说的那种疑惑，那种模糊但确凿存在的，一片庞大氤氲的水气。")]),_._v(" "),v("h2",{attrs:{id:"_6-娱乐的生活扼死好奇心"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_6-娱乐的生活扼死好奇心"}},[_._v("#")]),_._v(" 6. 娱乐的生活扼死好奇心")]),_._v(" "),v("p",[_._v("但现在的人不爱水气，爱骄阳。今天的人最好谈好奇心，怕是我们确实最缺乏好奇心。")]),_._v(" "),v("p",[_._v("多了解几个漫威英雄恐怕算不上好奇心，开头我描述的那段星巴克对话，对文戏的缺乏耐心和所谓“文艺电影”的厌恶，其实是对“理解门槛”的厌恶。")]),_._v(" "),v("p",[_._v("美队和敌人大打一架没啥难理解的，灭霸要毁灭一半的人，因为人多了就容易没了秩序（甚至有人认为这也挺深刻的）也很好理解。")]),_._v(" "),v("p",[_._v("在我的观察中，今天的人的确没有好奇心，稍有理解门槛的东西，不管是书籍还是电影，甚至一篇公众号文章，他们都不想投入精力。")]),_._v(" "),v("p",[_._v("当然，你也无法在流行着抖音、多倍速播放、网络小说的氛围里，苛求人们竟然还可以保住好奇心。")]),_._v(" "),v("p",[_._v("这当然回答了2011年后好电影消失的原因，答案非常简单，因为我们失去了对新东西的兴趣，不再重视好电影带来的价值。")]),_._v(" "),v("p",[_._v("类型片与非类型片仅仅是两种平行的“趣味”，没有高下之分，后者没有市场，前者名利双收。拍好电影“太不划算”。")]),_._v(" "),v("p",[_._v("正如另一个我们经常听到的例子一样，我想观众也只会获得他们配得上的电影，没有好电影的真正原因，是观众不配，丧失观赏好电影观众的时代，自然是好电影消失的时代。")]),_._v(" "),v("p",[_._v("我想我的观点没那么难接受，今天人们确实是一边以人类从未有过的密度娱乐着，一边深深地感受着娱乐后的空虚和无聊。")]),_._v(" "),v("p",[_._v("这是一种深深的矛盾。")]),_._v(" "),v("p",[_._v("娱乐的生活是扼死好奇心的，是“改变”的敌人，是一间舒适的“空调房”，那里面不光提供快感，也提供自我陶醉。")]),_._v(" "),v("p",[_._v("但娱乐确实并不让人真正满足，它是一种永远吃不饱的假粮食，是安慰剂。")]),_._v(" "),v("p",[_._v("当然我们也明白，在我们每一个热血上脑，每一个自我陶醉，每一个深感快乐的空虚时刻背后，也都有一个数钱的人，在我们的堕落和恶习上，构筑他的权力和王国。")]),_._v(" "),v("h2",{attrs:{id:"_7-电影的-作用"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_7-电影的-作用"}},[_._v("#")]),_._v(" 7. 电影的“作用”")]),_._v(" "),v("p",[_._v("作为生活单调无趣的现代人，电影能够有什么作用？当然回答这个问题的思路已经蕴含在其假设中了，当代人经验极其单调，被封闭在资本主义晚期文化中，城市多彩纷呈但千篇一律。")]),_._v(" "),v("p",[_._v("人们不真诚地在无聊中渴求着“新东西”。")]),_._v(" "),v("p",[_._v("但它可能是电影吗？电影能不能为我们带来“新东西”？且不是一种“潜移默化”的难以感受的冲击，而是一种实质的影响。")]),_._v(" "),v("p",[_._v("在我看来，不仅是可能的，而且电影是我们最可以依靠的“新东西”。")]),_._v(" "),v("p",[_._v("福柯有一部文集，中译本出版名为《福柯看电影》，在其中，他也表达了相当类似的看法（这将是下一篇文章的内容了）。")]),_._v(" "),v("p",[_._v("我在最后推荐了十部电影，这些电影都对我起到了立竿见影的实际影响，很多甚至彻底改变了我的人生选择。")]),_._v(" "),v("p",[_._v("这里面没有特别偏门的电影，都是比较经典和脍炙人口的，肯定会有铁杆的电影爱好者认为这个推荐的“逼格”太低，不过我个人确实没有涉猎太过偏门的小众电影，也就很诚实地推荐这些吧。")]),_._v(" "),v("h2",{attrs:{id:"本期影单推荐"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#本期影单推荐"}},[_._v("#")]),_._v(" 本期影单推荐")]),_._v(" "),v("ul",[v("li",[_._v("《潜行者》安德烈•塔克夫斯基 1979")]),_._v(" "),v("li",[_._v("《永恒和一日》西奥•安哲罗普洛斯 1998")]),_._v(" "),v("li",[_._v("《背靠背，脸对脸》黄建新／杨亚洲 1994")]),_._v(" "),v("li",[_._v("《末代皇帝》贝纳尔多•贝托鲁奇 1987")]),_._v(" "),v("li",[_._v("《绝美之城》保罗•索伦蒂诺 2013")]),_._v(" "),v("li",[_._v("《皮罗斯马尼》格奥尔基•申格拉亚 1969")]),_._v(" "),v("li",[_._v("《杀人回忆》奉俊昊 2003")]),_._v(" "),v("li",[_._v("《一次别离》阿斯哈·法哈蒂 2011")]),_._v(" "),v("li",[_._v("《窃听风暴》弗洛里安·亨克尔·冯·多纳斯马尔克 2006")]),_._v(" "),v("li",[_._v("《一一》杨德昌 2000")])])])}),[],!1,null,null,null);v.default=a.exports}}]);