(window.webpackJsonp=window.webpackJsonp||[]).push([[67],{393:function(_,v,p){"use strict";p.r(v);var t=p(7),a=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("“爆炸”被认为是个有说服力的词汇，宇宙大爆炸，寒武纪生命大爆炸，刘慈欣《三体》中的技术爆炸，我们面临的信息爆炸。")]),_._v(" "),v("p",[_._v("这里“爆炸”的含义大概都是说短时间内丰富性和数量的巨大发展。但若面向看理想的读者发问，“信息爆炸”是一件好事吗？")]),_._v(" "),v("p",[_._v("我想这个问题本身就带有巨大的引导性，大多数人会说出信息爆炸不好，还能够立即语义性的说出诸如“信息过载”这样的词汇。更有“批判性思维”的读者还能迅速说出，“信息爆炸”是中性的，好或者坏取决于使用者以什么态度对待“信息爆炸”这样绝对正确的废话。")]),_._v(" "),v("p",[_._v("那这到底是一场什么样的爆炸？我们该继续在以上“爆炸”的语用中理解他，还是换到一些别的爆炸呢？例如“核爆炸”。")]),_._v(" "),v("h2",{attrs:{id:"_1-欢迎来到这片信息核爆后的废土"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_1-欢迎来到这片信息核爆后的废土"}},[_._v("#")]),_._v(" 1. 欢迎来到这片信息核爆后的废土")]),_._v(" "),v("p",[_._v("观赏任何废土题材的电影，总是很新鲜，但想象一个信息核爆后的废土世界，就能了解实际身处废土之中，我们还是熟视无睹的。")]),_._v(" "),v("p",[_._v("最近半年有很多令我倍感惊讶的现象，我大概总结了一下，可以说“年轻人网络习惯的大龄化”，可能包括：一、如此多人经常关注朋友圈微商的商品；二、年轻人信息和见识竟然如此封闭；三、年轻人以自然语言而非关键词的方式使用搜索引擎。")]),_._v(" "),v("p",[_._v("当然衍生的现象也显著的，例如微信“在看”这个我从来不明白缘由的功能，似乎也有不少人在使用。")]),_._v(" "),v("p",[_._v("欢迎来到这片信息核爆后的废土。这里有不洁净的饮用水，废弃的遗迹，极端的天气。")]),_._v(" "),v("h3",{attrs:{id:"信息污染的饮用水"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#信息污染的饮用水"}},[_._v("#")]),_._v(" - 信息污染的饮用水 -")]),_._v(" "),v("p",[_._v("作为一个前互联网时代的人，我是个“下载党”。")]),_._v(" "),v("p",[_._v("这意味着不管是电子书籍，视频，音乐，如果不能够下载到本地，我一般不会使用。这导致我家里有数量可观的移动硬盘，用以储存这些已经一再精简的内容。")]),_._v(" "),v("p",[_._v("我知道这样做有点矫情，不过下载的过程就像是一台“净水器”，让广告、推荐、评论、弹幕，或是基于所有网页和APP流程的一切操作都得以消除，我得以还原一个简朴的与作品的接触。")]),_._v(" "),v("p",[_._v("我觉得这是很多人依然依恋纸质书籍的缘由，虽然读者也不自知的使用“质感”来描述这个依恋。但我要说，其实读者要的是“纯粹”，读者不希望阅读中连带着一套UX操作，其他读者痕迹，广告，推送或是手机上任何其他APP不断的提醒打扰。")]),_._v(" "),v("p",[_._v("试想过去我们听音乐的方式，将一盘磁带放入播放器，除了暂停、快进和快退，你什么也不能做。现在呢？可以点击这个歌手，可以点击这张专辑，可以浏览评论区，红心，收藏，和这首歌相似的，基于这首歌创建电台，还可以分享到朋友圈或者微信群组。更不用提里面他人的歌单，软件每日推荐的歌曲。")]),_._v(" "),v("p",[_._v("那些之前承诺提供“纯净体验”的APP都去哪里了？为何互联网不可能有“纯净体验”呢？这也许是下一篇文章的话题了。")]),_._v(" "),v("p",[_._v("这是一种“强迫症”吗？或仅仅是一种“审美情调”？")]),_._v(" "),v("p",[_._v("信息污染可以在下列的例子中呈现，点击一部电影前播放的广告可以被视作一种“污染”，但B站电影的“弹幕”往往不被视作一种“污染”。是否是“污染”当然不在于受众是否喜欢。不然，只要广告内容贴近电影的内容，同时兼具趣味，广告是否也可以不被视作污染了呢？")]),_._v(" "),v("p",[_._v("这已经不是一个假设了，现在的综艺节目和电视剧早就完成了对广告植入的娱乐化改造，我们可以“事后诸葛亮”地感受到，其实综艺和电视剧甚至电影中广告植入的定制化和娱乐性，也是发展的必然，广告体验会成为一种娱乐方式，成为一种“弹幕式”的交互体验，想想李佳琦吧，这一天已然到来。")]),_._v(" "),v("p",[_._v("APP体验也是一样，签到，积分，巨大的数字，权益的设置，但这样这些就不是污染了么？")]),_._v(" "),v("p",[_._v("污染物即便是可口的，同样是污染。或许，就像可口可乐和炸鸡一样吧，资本主义的范式总是成套出现。")]),_._v(" "),v("p",[_._v("欢迎来到信息废土，饮用水的信息污染是必然的，数字化媒介的特性注定了信息可以无孔不入地互相渗透。")]),_._v(" "),v("p",[_._v("越是交互性强烈的氛围，纯净的信息就注定不可能存在，饮用包含广告，平台目的，其他用户目的的水源，是这个信息废土世界的必然。")]),_._v(" "),v("h3",{attrs:{id:"废弃的遗迹"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#废弃的遗迹"}},[_._v("#")]),_._v(" - 废弃的遗迹 -")]),_._v(" "),v("p",[_._v("像是废土世界上的那些遗迹——什么是遗迹呢？就是明显的错位，已经失去其原有价值的文明物件，例如变成酒吧的教堂，变成商场的博物馆。")]),_._v(" "),v("p",[_._v("像是上海无印良品旗舰店的收银台，我仔细辨认着构成收银台背景墙上的书籍，那是一墙的旧书，以科学专业书籍和哲学为主，都是很厚的精装本。以一种明显被设计的方式交错地摆放在一起，它们再也不被当作书籍看待，而是一种建筑材料，书籍被当作建材不是个新鲜现象。")]),_._v(" "),v("p",[_._v("那么书店被当作摄影棚呢？是诸位陌生的现象吗？")]),_._v(" "),v("p",[_._v("想想被“网红经济”摧毁的一切，一切的“网红XXX”，被当作摄影棚和朋友圈素材的，原本被当作餐厅，博物馆或任何目的的一切场所，成为一种遗迹，在信息核爆中被摧毁，留下仅仅残留外表的躯壳，被人们填充进他们的不管何种目的。")]),_._v(" "),v("p",[_._v("其实人也是一样，你们还记得上海的那位流浪汉吗？")]),_._v(" "),v("h3",{attrs:{id:"极端的天气"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#极端的天气"}},[_._v("#")]),_._v(" - 极端的天气 -")]),_._v(" "),v("p",[_._v("废土世界的想象中，极端天气的想象是与之伴生的，也许并没有什么科学道理，不过这反映出人准确的直觉——当秩序消失时，平衡总是无法维持，剧烈现象会无处不在。")]),_._v(" "),v("p",[_._v("科幻作品中的具象想象，只是现实世界的隐喻。")]),_._v(" "),v("p",[_._v("信息核爆后，言论风暴无处不在，去年因为手游《恋与制作人》而断货的福柯书籍《疯癫与文明》事件依然历历在目。当代环境的极端特征我想是我最不需要给予任何例子和阐述的。这已经构成了我们的日常经验。")]),_._v(" "),v("p",[_._v("只不过，我想《恋与制作人》并未增加多少福柯的读者，只不过徒增了不少文明遗迹罢了，毕竟，在这场信息核爆中，《疯癫与文明》虽然只有区区不到三百页，也太长太难了。")]),_._v(" "),v("h2",{attrs:{id:"_2-信息核爆不是一种再建构-而是一种强势的摧毁"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_2-信息核爆不是一种再建构-而是一种强势的摧毁"}},[_._v("#")]),_._v(" 2. 信息核爆不是一种再建构， 而是一种强势的摧毁")]),_._v(" "),v("p",[_._v("爆炸后为何会有“废土”，自然是因为爆炸力量太过巨大，摧毁了太多的东西。那么信息爆炸，摧毁了什么呢？")]),_._v(" "),v("p",[_._v("这个概念早在上世纪六十年代就被提出，不过在当时的视角，这还是一个“信息论”概念下的纯粹数量观念，意指信息的纯粹数量会爆发性增长。但到今天，信息爆炸的真正摧毁性，却不是数量爆发可以总结的。")]),_._v(" "),v("p",[_._v("信息核爆本身包含两个维度，一是工业性，二是普适性，这是一场庸俗的挤压和巨大膨胀。")]),_._v(" "),v("p",[_._v("工业模式带来的短周期特征和复制性是信息核爆的基础，创作周期过长的东西无法引爆，“微”和“短”是必须的，此种工业化流程加上受众数量的结构化剧增，例如一个APP的传播，就是信息的核爆炸。")]),_._v(" "),v("p",[_._v("工业化染指任何信息生产的地方，引爆短周期制作物的几何增长，10分钟或者更短的视频，基于稳定行为的直播，篇幅极短的图片与字（极端形态就是表情，因此很多爆火的公众号漫画其本质是拉长的网络表情），几百字逐行空行加上图片的文章，美图软件的自拍，数字软件制作的电子乐和口水歌，挑动情绪和争议的100多个字（不管是论坛还是微博）。我挖空心思想举出别的例子，还真没有了，这七种形式引爆，其他的一切都需要边缘化。")]),_._v(" "),v("p",[_._v("这里我们看到信息爆炸和本篇开头我们提到的诸多爆炸的极大区别，那些爆炸都伴随着多样性的巨大增长，而信息核爆是单一化的，因而是一种摧毁而非建构。")]),_._v(" "),v("p",[_._v("而普适性的特点是一场巨大的向下兼容，这个词听上去复杂，但原理很简单，我对康德感兴趣，我对吃也感兴趣，但一个对吃感兴趣的人，却未必对康德感兴趣。")]),_._v(" "),v("p",[_._v("这个世界确实有更普适的内容，这场爆炸需要向下兼容的风，需要让任何人都可以为之付出的30秒钟，一个再严肃生活的人，也总有付予给一点点笑料的30秒吧。只要你有这30秒，这场核爆就能将他变成一场无尽的重复。")]),_._v(" "),v("p",[_._v("所以信息爆炸不是所有信息的爆炸，这仅仅指工业化和普适性信息的爆炸，因此它摧毁的东西是显而易见的。")]),_._v(" "),v("p",[_._v("那些旧世界的文明遗迹，图书馆中陈列的皇皇巨著，音乐厅中演奏的篇章，精心制作的整张的专辑，长篇的小说，自然是这场风暴中被摧毁的事物。")]),_._v(" "),v("h2",{attrs:{id:"_3-平庸文化与相对主义不是一种消极立场-反而是一种强攻击性文化"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_3-平庸文化与相对主义不是一种消极立场-反而是一种强攻击性文化"}},[_._v("#")]),_._v(" 3. 平庸文化与相对主义不是一种消极立场， 反而是一种强攻击性文化")]),_._v(" "),v("p",[_._v("在废土世界，人们如何寻找一点生存的幻想呢？当然是恢复植被，他们“种草”。")]),_._v(" "),v("p",[_._v("“种草”一词2015年至今的搜索量")]),_._v(" "),v("p",[_._v("这是“种草”这个词汇从2015年到现在的搜索量，当然“关键词搜索量”已经慢慢失去了参考性，这是信息核爆中的又一个现象。但“种草”在最近几年确实日渐频繁。")]),_._v(" "),v("p",[_._v("“种草”是我们与这个复杂世界发生关联幻觉的微小时刻，通过一个被植入的“兴趣”，我们证明在这片废土上，我们还未完全丧失关心的能力。")]),_._v(" "),v("p",[_._v("对于依靠消费行为定义自我的现代人，“被种草”简直是具有存在性意义的行为。")]),_._v(" "),v("p",[_._v("不过这需要一种在这个信息废土中的末世心态，即对“种草”和“消费”一种近乎绝望的开放，对消费机会的敏感和内心打开，任由工业化量产信息任意对其形成扰动，这是一种很大的放弃，稍微强烈的优越感和自我都会彻底封死“种草”的可能。")]),_._v(" "),v("p",[_._v("前种草时代是“搜索”的，而种草时代是“推荐”的，这是两种截然不同的生态。")]),_._v(" "),v("p",[_._v("从这里，我们就能发现，他们的区别绝不在信息的多寡，在信息论式的信息爆炸时代，我们用关键词和搜索引擎已经完成了对信息的检索和选择的构建，是什么让我们甚至对此还不满意，并欣然接受了一套“推荐算法”的机制，并彻底进入种草时代呢？")]),_._v(" "),v("p",[_._v("说明信息核爆不仅摧毁了一些外在的文明构建，像是书籍成为建材，信息核爆还摧毁了每个人在内心构建文明建筑的可能。")]),_._v(" "),v("p",[_._v("现象也是简单的，网络舆论工业化地生产出对豆瓣的仇恨，对公共知识分子的讽刺，对一切装模作样的卓越追求的敌意，或是对任何边缘价值高速的污名化。")]),_._v(" "),v("p",[_._v("绝大多数的极端天气，我们所讲的风暴，都是对任何内心文明的恫吓。")]),_._v(" "),v("p",[_._v("在我的电台节目（《翻转电台》）中，我就说，平庸文化和相对主义不是一种与世无争的消极立场，相反，平庸文化与相对主义是一种强攻击性文化。那么信息爆炸的过程，就是文化的进击，对一切其他文化的恐吓。")]),_._v(" "),v("p",[_._v("因此，信息爆炸不是多元信息的等比增长，而是工业化普适信息对一切不符合此类信息的激进消灭。一个搜索的是世界是平等的，而一个推荐的世界不是，一个各取所需的世界是平等，而一个“种草”的世界是攻击性的。")]),_._v(" "),v("h2",{attrs:{id:"_4-信息辐射以语言符号为载体-继续强化偏见"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_4-信息辐射以语言符号为载体-继续强化偏见"}},[_._v("#")]),_._v(" 4. 信息辐射以语言符号为载体， 继续强化偏见")]),_._v(" "),v("p",[_._v("我们可以尝试删除微信，注销论坛，和信息核爆保持隔绝。但如同核爆炸一样，信息爆炸同样有辐射，是高浓度的。")]),_._v(" "),v("p",[_._v("这个类比在生活中成为“偏见”被我们在日常感受。信息爆炸制造偏见的能力与其普适性高度相关，这个符合从善如流，从恶如崩的朴素道理。仇恨与迁怒是向下兼容的，或是在一种巨大人群中的幻觉，也是向下兼容的。")]),_._v(" "),v("p",[_._v("每一场极端天气，都在强化偏见。")]),_._v(" "),v("p",[_._v("我们都听说过互联网的极化效应，认为互联网上仅仅能够容纳和传播极端观点，我倒觉得不仅如此。我们何时见过互联网环境对于某个公共事件呈现了极端包容和宽容的情绪？还是我们每一次都目睹极端不理解和惩罚欲望。")]),_._v(" "),v("p",[_._v("然后这样的偏见广泛地面向生活辐射，我在虎扑（我更愿意称其为“中国男性偏见网”）上看过不下十个帖子，内容为年轻的男孩将虎扑上充满偏见的帖子转发给自己的女友，或用虎扑上充满偏见的言论与女友争论，最终导致分手。而后他们将微信聊天记录转回虎扑，投入这场信息核爆，进一步扩散辐射。")]),_._v(" "),v("p",[_._v("我并非主张在信息爆炸之前，偏见从不存在，在任何时代我们都目睹过极其可怕的偏见。")]),_._v(" "),v("p",[_._v("但我主张一种我们可以体认的“偏见氛围”和“辐射式的偏见载体”，尤其是这些偏见在信息爆炸中凝结为层出不穷的语言符号，也就是迷因（MEME）的能力。")]),_._v(" "),v("p",[_._v("语言符号是信息辐射的实际载体，是偏见在信息爆炸过程中的结晶和辐射源。在过去的时代，这些结晶和口号是以中心化宣传的方式生产的，但在今天这些符号是在极端天气中自然结晶的。")]),_._v(" "),v("p",[_._v("其最显眼的形态就是对各种人群的污名化别称，不太显眼的是这些别称后凝结的巨大逻辑和对世界的扭曲理解，这种扭曲理解就是“辐射”。")]),_._v(" "),v("p",[_._v("其中最明显的例子甚至是“直男”这个词汇，你可以搜索你的朋友圈中使用“直男”的这个词汇，感受每个用例背后奇怪的假设和偏见。这是个多常用的词汇，回想我在生活中都在当面与他人的对话中多次使用过“我是个钢铁直男，因此……”的表述。")]),_._v(" "),v("p",[_._v("你可以隔绝媒介，但无法隔绝词汇的穿透力，这就是信息核爆辐射的影响。")]),_._v(" "),v("h2",{attrs:{id:"_5-这毫无疑问是个寒冷的时代"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_5-这毫无疑问是个寒冷的时代"}},[_._v("#")]),_._v(" 5. 这毫无疑问是个寒冷的时代")]),_._v(" "),v("p",[_._v("信息核爆中结晶词汇的穿透力已经带来一种无奈的感觉，我尽力不使用网络词汇，或者在使用网络词汇时注意的提示和说明这是个网络词汇。")]),_._v(" "),v("p",[_._v("但这样的抵抗有点乏力，信息爆炸最终呈现为核冬天的形态，辐射粉尘遮蔽大气层带来的寒冷。")]),_._v(" "),v("p",[_._v("信息爆炸会令真实性彻底被粉尘遮蔽，男性和女性之间个体化的张力被“直男癌”和“女拳”的信息辐射替代，更不必说人群与人群的关系，地区与地区的关系。")]),_._v(" "),v("p",[_._v("这毫无疑问是个寒冷的时代，被这些辐射源和粉尘塑造的偏见和仇恨是持存的，但爆炸中偶发的温情却是速朽的。")]),_._v(" "),v("p",[_._v("我想起有个早晨我们突然对自闭症儿童充满关怀，转发他们的画作，这种巨量的关怀持续到了当天下午就几乎消失殆尽了。但对明星的仇恨却能跨越漫长的时间延续，例如赵薇，她已经彻底的和“日本”以及“资本”这样的词汇连接，那是两场巨大的风暴，和随之而来的粉尘，已经几乎锁死了她的形象。")]),_._v(" "),v("p",[_._v("核冬天的特点就在于它的漫长，核子粉尘遮天蔽日也许要长达一年之久，而信息爆炸的偏见持存，会花上更长的时间。")]),_._v(" "),v("p",[_._v("更不必说我们经历的核爆炸是偶然的，但信息爆炸，还在以极高的频率一再发生。")]),_._v(" "),v("h2",{attrs:{id:"_6-批判性思维-失去了意义"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_6-批判性思维-失去了意义"}},[_._v("#")]),_._v(" 6. “批判性思维”失去了意义")]),_._v(" "),v("p",[_._v("在下面这个场合，人们不使用“种草”这个词汇，实际上他们应该如此。这个场合就是他们又因为某场网络风暴憎恨了一个不曾憎恨的人或者群体，最近也许是BBC。")]),_._v(" "),v("p",[_._v("这与种草的逻辑高度类似，人们对新鲜的消费欲望，这种向下兼容的动机保持着开放，同时也对新鲜的仇恨，这种向下兼容的动机保持着开放。")]),_._v(" "),v("p",[_._v("对某个商品种草，对某个人群仇恨，是一件事。")]),_._v(" "),v("p",[_._v("这时陈词滥调会求助于“批判性思维”或“独立思考”的能力，认为信息爆炸的解决之道在于每个人独立地在网络世界中明辨是非。")]),_._v(" "),v("p",[_._v("——我可以用最肯定的口吻说这毫无帮助，每一种偏见，对族群的偏见，对中医的偏见，对女权的偏见，甚至男性对自己的偏见，都已然建构在一套符合“批判性思维”的论理和证据之下，你去知乎随便逛逛，哪个偏见下面不是言之凿凿，引经据典？")]),_._v(" "),v("p",[_._v("在所有稍微精致一些的网络风暴里面，哪个不是事实充分，数据详实，逻辑缜密的。事实上大多数对于别的族群的偏见，尤其是对于“愚蠢”的偏见，恰恰是指责他们不够拥有“批判性思维”。")]),_._v(" "),v("p",[_._v("那么一个事实充分，数据详实，逻辑缜密的偏见，为什么我们依然认为那是个偏见？")]),_._v(" "),v("p",[_._v("所以信息爆炸到底炸毁了什么呢？")]),_._v(" "),v("p",[_._v("这个在星巴克里坐在我对面的男人，和我年纪相仿，他戴着一副价格不菲的眼镜，新款的Airpod Pro，有点胖，嘴巴微张着刷着手机。他自己随身带着一个手机充电器，坐下后立即接上电源，就这样，在我写这篇文章的3个小时中，他已经刷了抖音整整三个小时。")]),_._v(" "),v("p",[_._v("只不过他的女朋友也在旁边，他们就这样互相低着头，在各自的手机上度过了3个小时。可能这就是信息爆炸真正摧毁的东西吧。")])])}),[],!1,null,null,null);v.default=a.exports}}]);