(window.webpackJsonp=window.webpackJsonp||[]).push([[69],{393:function(_,v,p){"use strict";p.r(v);var t=p(7),a=Object(t.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("p",[_._v("距离21世纪20年代还有9天，不过对于绝大多数人而言，年代之交并无什么不同，每天依然按部就班。")]),_._v(" "),v("p",[_._v("不过我倒是在这个年代之交，想到了上世纪90年代与新世纪的时候，距今不过二三十年，但回过头看，却非常非常不同。")]),_._v(" "),v("p",[_._v("我想到今天这个娱乐的时代，电影，电视剧，网络小说，短视频，二三十年也许是个很合适的距离，因为那个时候，也有电影，小说，电视剧，音乐。")]),_._v(" "),v("p",[_._v("韩寒与周杰伦，都是在2000年，分别出版自己的第一本书，与第一张专辑。但这二十年，电影、音乐、文学，兴许已经彻底不同了。")]),_._v(" "),v("p",[_._v("我不想把这篇文章写成一个旧日不再的缅怀文章，就洗去一些感性的表述，从实际生活细节的表述，描述上世纪90年代的文艺生活，并与今天的生活形成对比。")]),_._v(" "),v("p",[_._v("也许在这个即将进入新十年的时候，回看过去，是最适合的事情。")]),_._v(" "),v("h2",{attrs:{id:"_1-回到2000-那还是以书籍塑造流行文化的年代"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_1-回到2000-那还是以书籍塑造流行文化的年代"}},[_._v("#")]),_._v(" 1. 回到2000， 那还是以书籍塑造流行文化的年代")]),_._v(" "),v("p",[_._v("如果你长期关注电商网站的畅销书排行榜，你就会发现所谓互联网的长尾效应已经实际上封锁了畅销书的领域。")]),_._v(" "),v("p",[_._v("2014年出版的《皮囊》，从2009年开始就大卖的《人间失格》，1998年就登上畅销榜第三名的《活着》（1999年这本书甚至都无法进入前十，而在今天依然是第五名），2006年就出版的《月亮与六便士》，从2014年畅销到今天的《解忧杂货铺》，从2015年畅销到今天的翻译本《浮生六记》，包括畅销十余年的《三体》。")]),_._v(" "),v("p",[_._v("这七本书，加上可能稍稍褪去热度的《追风筝的人》、《摆渡人》和《岛上书店》，几乎锁定了大多数书店进门最显眼位置的景观。")]),_._v(" "),v("p",[_._v("但在新世纪左右，情况却完全不是如此，彼时早期涉足图书电商的当当网刚刚成立，人们买书更多还是在线下，与今时独立书店“兵败如山倒”的局面相比，令人唏嘘。")]),_._v(" "),v("p",[_._v("1997年倪萍出版的《日子》登上畅销书榜首，次年春晚崔永元，赵本山和宋丹丹的小品还用到这本书的梗，模拟写出了《月子》；")]),_._v(" "),v("p",[_._v("1998年是余秋雨的《山居笔记》，那些年每年余秋雨发表新书还是一件不小的事件；")]),_._v(" "),v("p",[_._v("1999年钱锺书集发布，本书登顶，第二名是彼时仍然写作的王朔，出版了他的新小说《看上去很美》；")]),_._v(" "),v("p",[_._v("2000年是痞子蔡的《第一次亲密接触》，我想大量的95后与00后甚至已经没有听说过这个人；")]),_._v(" "),v("p",[_._v("2001年是一本畅销到今天的书籍《穷爸爸富爸爸》，这是唯一可以从那个年代畅销至今的题材，这也是个有意思的话题；")]),_._v(" "),v("p",[_._v("2002年，是同样财富向书籍的《谁动了我的奶酪》登上畅销榜首；")]),_._v(" "),v("p",[_._v("随后，2003年是横空出世的JK.罗琳和她的《哈利·波特与凤凰社》。")]),_._v(" "),v("p",[_._v("这些书名让年纪稍大的读者可以立即回到新世纪前后的年代，和当时的生活点滴。")]),_._v(" "),v("p",[_._v("在那个年代，每年的新书发布拥有今天电影发布那样的影响力，我记得每次《哈利·波特》新书上市时，整座城市都是这个漫画版的孩子和他额头的闪电。")]),_._v(" "),v("p",[_._v("畅销书排行榜的停滞当然直观地反映出我们对阅读本身的漠视，以上的10本书因为它们的名字和名气成为大多数人投入阅读习惯的“入门十本”。但这个入门漫长而难以完成，除这十本之外，实在再无其他的阅读时间。")]),_._v(" "),v("p",[_._v("这当然不是说新世纪前后的人比今天的人更自律或是爱知求真。只是彼时距离中国电影院线制的建立，和随后点燃大陆电影市场的张艺谋的《英雄》上市，还有两年时间。")]),_._v(" "),v("p",[_._v("家用互联网当时既贵且慢，OICQ（QQ的前身）上市刚刚不到一年，网络小说还未兴起。我上面提到的一切畅销书，就是当时的“流行文化”，那还是一个以书籍塑造流行文化的年代。")]),_._v(" "),v("p",[_._v("新世纪前后，余秋雨的文化作品，刘晓庆、倪萍、白岩松等人的名人书籍，王朔等作家的书籍，是大众流行文化的风向标。当然最直观的原因是经济尚不发达，出版业比起电影和音乐，都是一个产值天花板较低的产业，和“互联网视频”产业更是不值一比。")]),_._v(" "),v("p",[_._v("这也是我写作本文的原因，我绝对不是想说“一代不如一代”。而是想说一种“时代的运气”，我出生的时代赶上了某种好运，这在今天已经变成了一种危机。")]),_._v(" "),v("p",[_._v("如果通过此文我们发现了这个断裂，那么彼时的好运气，可以成为我们今天的主动塑造的东西。")]),_._v(" "),v("p",[_._v("尤其是在将要到来的20年代。")]),_._v(" "),v("h2",{attrs:{id:"_2-属于新世纪前后的好运-对-反叛-的新东西有期待-有宽容"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_2-属于新世纪前后的好运-对-反叛-的新东西有期待-有宽容"}},[_._v("#")]),_._v(" 2. 属于新世纪前后的好运—— 对“反叛”的新东西有期待，有宽容")]),_._v(" "),v("p",[_._v("新世纪前后的人是好运的，青年文学陪伴了他们的青春期。")]),_._v(" "),v("p",[_._v("别误会，我当然不认为青年文学有多么高的艺术和思想价值，但也许人的成长确实也有个过程，期待人在青春期的时候就对《浮士德》或《红楼梦》产生巨大兴趣并真的从中得到本真性的震撼，恐怕也是揠苗助长。我们那代人，也许恰好得到了合适的东西。")]),_._v(" "),v("p",[_._v("那是人们对严肃题材与文字美感，都尚有兴致与尊重的年代。")]),_._v(" "),v("p",[_._v("彼时著名的“网络作家”安妮宝贝，在2000年出版她的第一本文集《告别薇安》，如果今天再回头看这本书，其文字的精致程度当然远超今天的很多出版物，更遑论今日的网络文学了。")]),_._v(" "),v("p",[_._v("随着新世纪的降临，85前后出生的人进入青春期。当时的青年文学当然清一色的恋爱题材，不管是2000年的《第一次亲密接触》，同年韩寒的《三重门》，还是2002年郭敬明出版他的第一本文集《爱与痛的边缘》，恋爱都是其中的主轴。")]),_._v(" "),v("p",[_._v("这听上去很自然，但其实不容易，当年在文学里大谈特谈恋爱，是有点离经叛道的。")]),_._v(" "),v("p",[_._v("因此，安妮宝贝在她最早的写作生涯中，因为颓废和对情爱的关注，还是个很“极端”的角色。但可能，新世纪的到来让人们对“新东西”有了更多期待和宽容。")]),_._v(" "),v("p",[_._v("《萌芽》这本杂志，和它主导的“新概念作文大赛”，是对“传统作文”形式的一个极大反叛。")]),_._v(" "),v("p",[_._v("但在当时，这场比赛与全国几乎所有知名大学合作，也受到一众文学前辈的关注和理解，是很不容易的。韩寒、郭敬明、张悦然等，都是这个生态孵化出的文学明星。")]),_._v(" "),v("p",[_._v("青春期中的人总是有过剩的冲动和精力要消耗，其中很大部分消耗在文艺作品上，可以是文学，是电影，是网络小说，是二次元，是偶像崇拜。我尝试说出的好运之一，就是新世纪前后的青年文学，确实提供了一种合适的文学养分。")]),_._v(" "),v("p",[_._v("对于不同时代的文艺形式，最安全的答案当然是，他们都是受到时代局限，各有特点，没有高下之分。我当然不这么看，当然在微信的文章中，也不做复杂的论述，我指出其中一个最大的差异。")]),_._v(" "),v("p",[_._v("那就是模仿的可能。")]),_._v(" "),v("p",[_._v("在初中时代，我的同龄人几乎半数都在尝试写作自己的“青春小说”，读完新概念作品，看着这些比自己仅仅大三四岁的人所取得的名声，蠢蠢欲动。要说当时的中学生没有憧憬自己可以在“新概念作文”比赛崭露头角，是不太可能的。")]),_._v(" "),v("p",[_._v("而且他们的文字成为某种“青春期情绪”的表达范本，新世纪前后的年轻人都在用文学的方式梳理和塑造自己的青春期表述，没有“表情包”，没有“流行语言”，更没有今天网文的既定题材和范式。")]),_._v(" "),v("p",[_._v("大家都成篇幅的，学着韩寒的一点点老成和批判，郭敬明的一点点细腻情感，写出积累起来篇幅不短的各种文字。")]),_._v(" "),v("p",[_._v("文字当然是最容易创作的。对比二次元的一代，或是饭圈文化一代。我们首先没有感受到那种巨大的门槛，不管是B站上骇人的播放量，还是明星们在微博上可怕的转发数。")]),_._v(" "),v("p",[_._v("当时的年轻人“粉丝”的观念很弱（这应当是2005年“超级女声”后逐渐兴起的文化），喜欢一位青年作家首先不意味着是他的“粉丝”或附庸，首先不代表着要去网络上捍卫他或表达对他的喜欢。")]),_._v(" "),v("p",[_._v("彼时阅读青年文学，首先意味着能够写出类似的作品，这至少是一种创造性的文化倾向，到今天，已经彻底沦为一种消费性的文化倾向。")]),_._v(" "),v("p",[_._v("我愿意说得明白一些，我想即便有人在青春期阅读《浮士德》和《红楼梦》，但不大可能产生模仿着写出这么一部鸿篇巨著的念头。今天的年轻人当然有不少想模仿着写出网络小说的，但那更多是对名利的渴望，今天的网络小说，已经远离对青春期生活的观照。")]),_._v(" "),v("h2",{attrs:{id:"_3-那时的年轻人更习惯与作品独处-沉淀下来的情绪-逼迫着自我表达"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_3-那时的年轻人更习惯与作品独处-沉淀下来的情绪-逼迫着自我表达"}},[_._v("#")]),_._v(" 3. 那时的年轻人更习惯与作品独处， 沉淀下来的情绪，逼迫着自我表达")]),_._v(" "),v("p",[_._v("这种本真的创造性从何而来呢？我想从“二刷”说起。")]),_._v(" "),v("p",[_._v("在今天，二刷一部电影已经是对电影质量莫大的肯定，二刷一部小说是不可思议的。")]),_._v(" "),v("p",[_._v("这当然首先不是质量的原因，而是可消费的文艺作品数量爆炸，永远有看不完的新东西在前面等待着。")]),_._v(" "),v("p",[_._v("但是在新世纪前后，二刷一部小说是寻常现象，不管是《哈利·波特》还是青春文学，读两三遍是很自然的，因为当时可看的东西实在不多。这初听上去是一种匮乏，但也许适度匮乏才是真正的好运。")]),_._v(" "),v("p",[_._v("试想你第二次阅读韩寒的作品，并没有一个网络纷争等待着你去参加，和你周围的同学也没什么太多可聊。“没什么太多可聊”对今天的人是很奇怪的，对于文艺作品，不是最可聊的么？")]),_._v(" "),v("p",[_._v("我想提示一点，今天我们有一种非常奇怪的文化——文艺评论的文化。")]),_._v(" "),v("p",[_._v("变着花样讨论“文艺作品”，在今天稀松平常，但这个能力，不是主动掌握的，而是被“教会”如此做的。是我们看过、听过了无数煞有介事的讨论，并学会了一套“语汇”，掌握了一套“讨论维度”后，才有的能力。")]),_._v(" "),v("p",[_._v("但在《三重门》的年代，年轻人们还讷于对这些东西夸夸其谈，互相说得出口的，不过是喜欢里面的某个人物，或对这部小说的整体的喜欢与否。要再多谈一些，也谈不出来了。今天二刷电影是挖掘其中可以分析的细节，但当时二刷一部小说，仅仅是再次与其中的情绪碰撞。")]),_._v(" "),v("p",[_._v("那时的年轻人，与作品是独处的，这不是个浪漫化的表述——我恰恰想说，只有这种独处，作品与他的关系，才是特别的，才能够和他自己的生活发生联系，并助长他自己的情感，直到这种情感大到需要表达出来的地步。")]),_._v(" "),v("p",[_._v("所以属于新世纪前后的那种“好运气”的二刷，是当时年轻人无法逃避的，与自己青春期困苦和压抑的独处与引导，他们不断夸大和发酵着青春期阴郁的情感，让人想要“为赋新词强说愁”。")]),_._v(" "),v("p",[_._v("虽然在原诗里，这句话是在说一种肤浅，不过若肤浅不在这个阶段充分表达并推进，再长大一些，也总会以别的方式呈现出来。")]),_._v(" "),v("p",[_._v("被沉淀下来的“青春期肤浅”逼迫着表达，长篇大论的，没有图片，没有网络流行语，必须用自己的话讲出来，是我们那代人的好运气。")]),_._v(" "),v("h2",{attrs:{id:"_4-现如今-人人都是-品评家-难再见真正的-表达者"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_4-现如今-人人都是-品评家-难再见真正的-表达者"}},[_._v("#")]),_._v(" 4. 现如今，人人都是“品评家” 难再见真正的“表达者”")]),_._v(" "),v("p",[_._v("表达者是好运的。")]),_._v(" "),v("p",[_._v("我想今天的人也都认为自己表达很多，因而不知道我在说的是什么。")]),_._v(" "),v("p",[_._v("今天说起一部电影，年轻人对里面的“梗”了如指掌，节奏、摄影、技法派表演、体验派表演，导演的过去，描述的时代，人人都是评论家。说到二次元或游戏，他们了解得更多。")]),_._v(" "),v("p",[_._v("我当然也曾经对这些颇有兴趣，也做过这样的“分析”，不过在这个时代之交，我可能要指出一个区别，并表示我以后会尽量远离这些文艺评价，我不再做了，也不再看了。")]),_._v(" "),v("p",[_._v("因为这是注意力的一个错误放置。")]),_._v(" "),v("p",[_._v("很会评论、分析、拆解，将心思花在结构性给出“评价”这件事上，尤其是“客观”的评价。你可能就已经阻断了自己表达的渠道。")]),_._v(" "),v("p",[_._v("对于青春文学，两三次看完一部青春小说后，脑子里仅有书中的情绪和遭遇，与当时自己青春期处境的共鸣。对于作品本身，并无表达欲望，但对于自己的生活，对于自己本来繁杂的情绪，却找到了很多可以言说的启发和范本。")]),_._v(" "),v("p",[_._v("今天的人看电影不是如此，在豆瓣写影评，如果联系自己的生活经历，会显得“太不专业”。专业性，这让我们看到影视剧作为文艺一枝独秀的一个原因，一种“言说的公共性”——")]),_._v(" "),v("p",[_._v("如果我们暂停下来稍微想想，就会发现一种人人都是“影评家（漫评家，游评家）”的倾向。")]),_._v(" "),v("p",[_._v("谁的演技更好？哪部作品的编剧更好？什么是摄影，什么是服化道，甚至可以分出“场面调度”的高下，联系时代性，给出电影意义的深刻剖析。赫然对于音乐和文学，我们还没有掌握这样一套“语汇”。")]),_._v(" "),v("p",[_._v("只要去到虎扑（被我称作“中国直男偏见网”），就能感觉到对于主流男性而言，对一部电影言说已经成为他们生活中多么重要的部分。")]),_._v(" "),v("p",[_._v("反观新世纪前后，那是表达与理论远远不如现在精致的年代，面对韩寒或郭敬明，这个作品好不好，是不是抄袭，根本不在彼时年轻人的关注中。")]),_._v(" "),v("p",[_._v("事实上，我确实对此很惊讶，对于韩寒和郭敬明是否抄袭一事，人们竟然投入了如此大的热情。这显然说明，对韩寒和郭敬明形成一个“意见”，已经成为了他们接触作品时最自然的期待。")]),_._v(" "),v("p",[_._v("这是一种巨大区别，今天的人看待一个文艺作品，是将其看作一个“评价对象”，而他自己是个“拿意见的人”。")]),_._v(" "),v("p",[_._v("自我是在对文艺作品的评价和好恶中树立的，每个接触文艺作品的人，重要的在于他的“鉴赏力”，这就像是他是否会品酒品茶。“品电影”是高级文艺消费的一种能力。")]),_._v(" "),v("p",[_._v("而在我的青春期接受“青春文学”的人，恰恰不是这样的心态，这会形成对于今天一个非常宝贵的对应视角。")]),_._v(" "),v("p",[_._v("在那个年代，对文艺作品的接触，尤其是对文学的接触，每个人都将自己看作一个“表达者”，重要的是作品在何种程度上与我的表达形成共鸣，并促进自己可以以类似方式开展和完成自我表达，我没什么评价要做，只有我憋着的满肚子情感，想要说出口。")]),_._v(" "),v("ol",{attrs:{start:"5"}},[v("li")]),_._v(" "),v("p",[_._v("站在新世纪前后，")]),_._v(" "),v("p",[_._v("我对一个“品评时代”保持警惕")]),_._v(" "),v("p",[_._v("我据此反思和反对一切“评价”产业，这不是个常见的反思，但站在与新世纪前后对比之上，我才发现这个巨大的断裂。")]),_._v(" "),v("p",[_._v("对电影、书籍、音乐、游戏等一切的打分，评论，有个隐而不显的前提假设。")]),_._v(" "),v("p",[_._v("即，我们每个人需要对我们消费的“文艺作品”拿出自己的意见，这是重要的，喜欢一本书，或不喜欢一本书，为何喜欢，为何不喜欢，甚至成为我们构建自我的关键。")]),_._v(" "),v("p",[_._v("能不能谈出一部电影的好，从而捍卫住一部电影的高分和评价，成为一个人的关键能力。")]),_._v(" "),v("p",[_._v("每一次豆瓣打分，在任何地方煞有介事地谈论文艺作品，按照一些术语和结构分析其好坏，越来越将主体塑造为一个“有个人好恶的品评者”。")]),_._v(" "),v("p",[_._v("说到底，喜欢一个东西，或讨厌一个东西，这又算得了什么？这不过是生活实践中不起眼的消遣，我们从中能够获得的，寡淡而稀薄。")]),_._v(" "),v("p",[_._v("这促使我们一部一部地读下去，看下去，打分下去，最后这整体的数量甚至成为一个炫耀的统计学依据。当然，这样一部部读下去，看下去，打分下去，也最终成为别人的生意。")]),_._v(" "),v("p",[_._v("这是娱乐产业的二次攫取，第一次攫取我们对作品本身的消费，再一次攫取我们对作品本身评价的消费。")]),_._v(" "),v("p",[_._v("想想你平时所看，电影，电视剧，然后再看其背景和解析，再看其评价与分析。这加在一起，可能已经花去你相当多的时间。但没关系，这些内容源源不绝，如果你想当个“文艺作品品评者”，你已经生在最繁荣的时代。")]),_._v(" "),v("p",[_._v("这说明文艺作品“公共得过了头”。")]),_._v(" "),v("p",[_._v("因为一部分公共性的压抑，让我们将公共性一股脑地灌在文艺作品的好恶上，这最终异化了我们与文艺作品的关系。")]),_._v(" "),v("p",[_._v("赫然，在新的十年，这一切品评的文化，会愈演愈烈。")]),_._v(" "),v("p",[_._v("但还好，这是我们的偏好与选择兴起的文化，也可以由我们自由地抛弃。")])])}),[],!1,null,null,null);v.default=a.exports}}]);